DECLARE\n    v_sql_errm varchar2(2048);\nBEGIN\n    UPDATE my_table SET my_column = do_something(my_column)\n        WHERE my_column IS NOT NULL;\nEXCEPTION\n    when others then\n        FOR i IN 1..SQL%BULK_EXCEPTIONS.COUNT LOOP\n            v_sql_errm := SQLERRM(-SQL%BULK_EXCEPTIONS(i).ERROR_CODE);\n            insert into log_error (msg) values ('Error updating value (' ||\n                SQL%BULK_EXCEPTIONS(i).ERROR_INDEX || '): ' || v_sql_errm);\n        END LOOP;\nEND;