-- Create tables with ON UPDATE CASCADE\nCREATE TABLE Contract (\n    ContractID INT PRIMARY KEY,\n    ContractNumber INT\n);\n\nCREATE TABLE RelatedTable (\n    RelatedID INT PRIMARY KEY,\n    ContractID INT,\n    FOREIGN KEY (ContractID) REFERENCES Contract(ContractID) ON UPDATE CASCADE\n);\n\n-- Update the contract number\nUPDATE Contract SET ContractNumber = 'new_value' WHERE ContractID = 'existing_id';