SELECT \n  SUM(CASE \n        WHEN (StartDate IS NULL OR StartDate <= '2009-01-05 00:00:00') \n         AND (FinishDate IS NULL OR FinishDate >= '2009-01-05 00:00:00') \n        THEN 1 \n        ELSE 0 \n      END) AS Week1,\n  SUM(CASE \n        WHEN (StartDate IS NULL OR StartDate <= '2009-01-12 00:00:00') \n         AND (FinishDate IS NULL OR FinishDate >= '2009-01-12 00:00:00') \n        THEN 1 \n        ELSE 0 \n      END) AS Week2,\n  SUM(CASE \n        WHEN (StartDate IS NULL OR StartDate <= '2009-01-19 00:00:00') \n         AND (FinishDate IS NULL OR FinishDate >= '2009-01-19 00:00:00') \n        THEN 1 \n        ELSE 0 \n      END) AS Week3,\n  SUM(CASE \n        WHEN (StartDate IS NULL OR StartDate <= '2009-01-26 00:00:00') \n         AND (FinishDate IS NULL OR FinishDate >= '2009-01-26 00:00:00') \n        THEN 1 \n        ELSE 0 \n      END) AS Week4\nFROM \n  tBillboards