-- Create multiple databases\nCREATE DATABASE DB1;\nCREATE DATABASE DB2;\nCREATE DATABASE DB3;\n\n-- Create tables in each database\nUSE DB1;\nCREATE TABLE Customers (\n    ID INT PRIMARY KEY,\n    Name VARCHAR(50)\n);\n\nUSE DB2;\nCREATE TABLE Orders (\n    ID INT PRIMARY KEY,\n    CustomerID INT,\n    OrderDate DATE\n);\n\nUSE DB3;\nCREATE TABLE OrderDetails (\n    ID INT PRIMARY KEY,\n    OrderID INT,\n    ProductID INT,\n    Quantity INT\n);