CREATE TRIGGER trg_SetDefaultTime\nON YourTable\nAFTER INSERT\nAS\nBEGIN\n    UPDATE t\n    SET TimeTaken = CASE WorkCategory\n        WHEN 'A' THEN 1\n        WHEN 'B' THEN 2\n        -- Add more cases as needed\n        ELSE 0  -- Default value if no match found\n    END\n    FROM YourTable t\n    INNER JOIN inserted i ON t.PrimaryKey = i.PrimaryKey\n    WHERE t.TimeTaken IS NULL;\nEND;