CREATE TABLE timezone_map (\n  old_code VARCHAR(5),\n  new_code VARCHAR(50)\n);\n\nINSERT INTO timezone_map (old_code, new_code) VALUES\n  ('10+', 'Australia/Sydney'),\n  ('1-', 'America/New_York'),\n  ('5+', 'Europe/London'),\n  -- Add more mappings as needed\n;\n\nUPDATE your_table\nJOIN timezone_map ON your_table.user_timezone = timezone_map.old_code\nSET your_table.user_timezone = timezone_map.new_code;