WITH ID1Values AS (\n  SELECT Values FROM YourTable WHERE ID = 1\n),\nID2Values AS (\n  SELECT Values FROM YourTable WHERE ID = 2\n)\nSELECT \n  CASE \n    WHEN ID1Values.Values IS NULL THEN ID2Values.Values + '(+)'\n    ELSE ID1Values.Values + '(-)'\n  END AS Result\nFROM \n  (SELECT Values FROM ID1Values EXCEPT SELECT Values FROM ID2Values) AS DeletedValues\n  FULL OUTER JOIN \n  (SELECT Values FROM ID2Values EXCEPT SELECT Values FROM ID1Values) AS AddedValues\n  ON DeletedValues.Values = AddedValues.Values