DELIMITER //\n\nCREATE PROCEDURE add_field_to_tables(field_name VARCHAR(255), data_type VARCHAR(255), table_names VARCHAR(255))\nBEGIN\n  DECLARE done INT DEFAULT FALSE;\n  DECLARE table_name VARCHAR(255);\n  DECLARE cur1 CURSOR FOR SELECT table_name FROM information_schema.tables WHERE table_schema = DATABASE() AND table_name IN (table_names);\n  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;\n\n  OPEN cur1;\n\n  read_loop: LOOP\n    FETCH cur1 INTO table_name;\n    IF done THEN\n      LEAVE read_loop;\n    END IF;\n\n    SET @sql = CONCAT('ALTER TABLE ', table_name, ' ADD COLUMN ', field_name, ' ', data_type);\n    PREPARE stmt FROM @sql;\n    EXECUTE stmt;\n    DEALLOCATE PREPARE stmt;\n  END LOOP;\n\n  CLOSE cur1;\nEND//\n\nDELIMITER ;