WITH VehicleAgeing AS (\n  SELECT \n    Ins_Comp, \n    dbo.fn_1To30Ageing(...) AS 1To30DaysAgeing, \n    dbo.fn_31To60Ageing(...) AS 31To60DaysAgeing, \n    dbo.fn_61To90Ageing(...) AS 61To90DaysAgeing\n  FROM Table\n  WHERE ....\n)\nSELECT \n  Ins_Comp, \n  1To30DaysAgeing, \n  31To60DaysAgeing, \n  61To90DaysAgeing, \n  1To30DaysAgeing + 31To60DaysAgeing + 61To90DaysAgeing AS TotalVehicles\nFROM VehicleAgeing