SELECT p.*\nFROM wp_posts p\nINNER JOIN wp_term_relationships tr ON p.ID = tr.object_id\nINNER JOIN wp_term_taxonomy tt ON tr.term_taxonomy_id = tt.term_taxonomy_id\nINNER JOIN wp_terms t ON tt.term_id = t.term_id\nWHERE \n    t.name IN ('Nuclear', 'Deals') AND tt.taxonomy = 'post_tag'\n    AND p.ID IN (\n        SELECT tr2.object_id\n        FROM wp_term_relationships tr2\n        INNER JOIN wp_term_taxonomy tt2 ON tr2.term_taxonomy_id = tt2.term_taxonomy_id\n        INNER JOIN wp_terms t2 ON tt2.term_id = t2.term_id\n        WHERE t2.name = 'Category1' AND tt2.taxonomy = 'category'\n    )\nGROUP BY p.ID\nHAVING COUNT(DISTINCT t.name) = 2;