WITH RECURSIVE related_skills AS (\n  SELECT SkillId, RelevantSkillId, RelevanceLevel, 0 AS level\n  FROM RelevantSkill\n  WHERE SkillId = 'Skill A' AND RelevanceLevel > 0.5\n  UNION ALL\n  SELECT r.SkillId, r.RelevantSkillId, r.RelevanceLevel, level + 1\n  FROM RelevantSkill r\n  JOIN related_skills s ON r.SkillId = s.RelevantSkillId\n  WHERE r.RelevanceLevel > 0.5\n)\nSELECT DISTINCT SkillId, SkillName\nFROM related_skills\nJOIN Skill ON related_skills.SkillId = Skill.SkillId;