CREATE TABLE Charts (\n    ChartId INT PRIMARY KEY,\n    ReportId INT\n)\n\nCREATE TABLE LineCharts (\n    ChartId INT PRIMARY KEY,\n    Thickness INT,\n    YAxis INT,\n    XAxis INT,\n    Color VARCHAR(10),\n    FOREIGN KEY (ChartId) REFERENCES Charts(ChartId)\n)\n\nCREATE TABLE PieCharts (\n    ChartId INT PRIMARY KEY,\n    Radius INT,\n    Color VARCHAR(10),\n    FOREIGN KEY (ChartId) REFERENCES Charts(ChartId)\n)\n\nCREATE TABLE BarCharts (\n    ChartId INT PRIMARY KEY,\n    Width INT,\n    Border INT,\n    FOREIGN KEY (ChartId) REFERENCES Charts(ChartId)\n)