-- Assuming your table is named 'linestrings'\nCREATE TABLE endpoints (\n  id INT,\n  point VARCHAR(50),\n  start_or_end ENUM('start', 'end')\n);\n\nINSERT INTO endpoints (id, point, start_or_end)\nSELECT id, \n       SUBSTRING_INDEX(linestring, ',', 1) AS start_point,\n       'start'\nFROM linestrings\nUNION\nSELECT id,\n       SUBSTRING_INDEX(linestring, ',', -1) AS end_point,\n       'end'\nFROM linestrings;