CREATE TRIGGER trg_MyTable_Update ON MyTable\nAFTER UPDATE\nAS\nBEGIN\n    IF EXISTS (\n        SELECT 1\n        FROM INSERTED i\n        JOIN DELETED d ON i.PrimaryKey = d.PrimaryKey\n        WHERE i.Column1 <> d.Column1 -- Check if Column1 was updated\n        AND i.Column2 = d.Column2 -- Check if Column2 was not updated\n        AND ...\n        AND i.Column150 = d.Column150 -- Check if Column150 was not updated\n    )\n    BEGIN\n        -- Only Column1 was updated, take action\n    END\nEND