DECLARE @sql nvarchar(max)\nDECLARE @tableName nvarchar(128)\n\nSELECT @tableName = 'myDb.dbo.multitableProc'\nSELECT @sql = 'CREATE TABLE #tempTable ('\n    + STUFF((SELECT ', ' + QUOTENAME(name) + ' ' + system_type_name\n             FROM sys.dm_exec_describe_first_result_set_for_object(@tableName, NULL)\n             FOR XML PATH(''), TYPE).value('.', 'nvarchar(max)'), 1, 2, '')\n    + ')'\n\nEXEC sp_executesql @sql\n\nINSERT #tempTable\nEXEC myDb.dbo.multitableProc 'A', 'B', 'C', 100