-- Identify the sessions using temp tablespace\nSELECT s.sid, s.serial#, s.username, s.program, t.tablespace\nFROM v$session s, v$sort_usage t\nWHERE s.saddr = t.session_addr;\n\n-- Identify the SQL statements using temp tablespace\nSELECT t.tablespace, s.sql_id, s.sql_text\nFROM v$sort_usage t, v$sql s\nWHERE t.sqladdr = s.address;\n\n-- Identify the top consumers of temp tablespace\nSELECT s.username, s.program, SUM(t.blocks) * tbs.block_size / 1024 / 1024 AS temp_usage_mb\nFROM v$session s, v$sort_usage t, dba_tablespaces tbs\nWHERE s.saddr = t.session_addr AND t.tablespace = tbs.tablespace_name\nGROUP BY s.username, s.program, tbs.block_size\nORDER BY temp_usage_mb DESC;