CREATE PROCEDURE YourProcedureName\n    @clientId INT\nAS\nBEGIN\n    DECLARE @cities TABLE (city VARCHAR(200));\n\n    INSERT INTO @cities (city)\n    SELECT DISTINCT city FROM tOH WHERE clientId = @clientId;\n\n    DECLARE @states TABLE (state VARCHAR(200));\n\n    INSERT INTO @states (state)\n    SELECT DISTINCT state FROM tOH WHERE clientId = @clientId;\n\n    SELECT * \n    FROM YourLargeTable\n    WHERE city IN (SELECT city FROM @cities)\n    AND state IN (SELECT state FROM @states);\nEND