-- Create a table to store the unique IDs\nCREATE TABLE UniqueIDs (\n    ID INT IDENTITY(1,1) PRIMARY KEY\n)\n\n-- Create a stored procedure to generate and retrieve the next available ID\nCREATE PROCEDURE GetNextUniqueID\nAS\nBEGIN\n    DECLARE @NextID INT\n\n    -- Insert a new row into the UniqueIDs table to generate the next ID\n    INSERT INTO UniqueIDs (ID) VALUES (NULL)\n\n    -- Retrieve the newly generated ID\n    SET @NextID = SCOPE_IDENTITY()\n\n    -- Return the next available ID\n    SELECT @NextID AS NextUniqueID\nEND\nGO