CREATE VIEW category_list AS\nSELECT \n  forum_categories.*,\n  MAX(CASE WHEN forum_answer.qtitle = forum_question.topic THEN forum_answer.a_id ELSE NULL END) AS latest_answer_id\nFROM \n  forum_categories\n  LEFT JOIN forum_question ON forum_question.catid = forum_categories.id\n  LEFT JOIN forum_answer ON forum_answer.question_id = forum_question.id\nGROUP BY \n  forum_categories.id;