CREATE PROCEDURE getEmployees \n    (@SortColumn nvarchar(100), \n     @SortDirection nvarchar(10))\nAS\nBEGIN\n    DECLARE @sql nvarchar(max) = N'\n        SELECT \n            EmployeeID, \n            FirstName, \n            LastName, \n            SSN, \n            Salary\n        FROM \n            Employees\n        ORDER BY ' + QUOTENAME(@SortColumn) + ' ' + @SortDirection\n\n    EXEC sp_executesql @sql\nEND