CREATE PROCEDURE getEmployees \n    (@SortColumn nvarchar(100), \n     @SortDirection nvarchar(10))\nAS\nBEGIN\n    SELECT \n        EmployeeID, \n        FirstName, \n        LastName, \n        SSN, \n        Salary\n    FROM \n        Employees\n    ORDER BY \n        CASE \n            WHEN @SortDirection = 'ASC' THEN \n                CASE \n                    WHEN @SortColumn = 'LastName' THEN LastName\n                    WHEN @SortColumn = 'Salary' THEN Salary\n                    WHEN @SortColumn = 'SSN' THEN SSN\n                    -- Add more columns as needed\n                END\n            ELSE \n                CASE \n                    WHEN @SortColumn = 'LastName' THEN LastName\n                    WHEN @SortColumn = 'Salary' THEN Salary\n                    WHEN @SortColumn = 'SSN' THEN SSN\n                    -- Add more columns as needed\n                END DESC\n        END\nEND