-- Create a sample table\nCREATE TABLE bbc (\n  name VARCHAR(50),\n  region VARCHAR(50),\n  population INT\n);\n\n-- Insert some sample data\nINSERT INTO bbc (name, region, population) VALUES\n  ('Country 1', 'Region 1', 100),\n  ('Country 2', 'Region 1', 0),\n  ('Country 3', 'Region 2', 200);\n\n-- Run the query with and without the AND population > 0 clause\nSELECT region, name, population\nFROM bbc x\nWHERE population >= ALL (\n  SELECT population\n  FROM bbc y\n  WHERE y.region = x.region\n);\n\n-- Incorrect results without the AND population > 0 clause\n-- Region 1 | Country 2 | 0\n\nSELECT region, name, population\nFROM bbc x\nWHERE population >= ALL (\n  SELECT population\n  FROM bbc y\n  WHERE y.region = x.region\n  AND population > 0\n);\n\n-- Correct results with the AND population > 0 clause\n-- Region 1 | Country 1 | 100\n-- Region 2 | Country 3 | 200