-- Object tables\nCREATE TABLE Authors (\n  Id INT PRIMARY KEY,\n  Name VARCHAR(255)\n);\n\nCREATE TABLE Books (\n  Id INT PRIMARY KEY,\n  Title VARCHAR(255)\n);\n\n-- Relationship tables\nCREATE TABLE AuthorBookRelationships (\n  AuthorId INT,\n  BookId INT,\n  PRIMARY KEY (AuthorId, BookId),\n  FOREIGN KEY (AuthorId) REFERENCES Authors (Id),\n  FOREIGN KEY (BookId) REFERENCES Books (Id)\n);\n\n-- Revision tables\nCREATE TABLE Revisions (\n  Id INT PRIMARY KEY,\n  ObjectId INT,\n  ObjectType VARCHAR(50),\n  RevisionNumber INT,\n  CreatedAt DATETIME,\n  Data TEXT\n);\n\nCREATE TABLE RevisionRelationships (\n  RevisionId INT,\n  RelationshipId INT,\n  PRIMARY KEY (RevisionId, RelationshipId),\n  FOREIGN KEY (RevisionId) REFERENCES Revisions (Id),\n  FOREIGN KEY (RelationshipId) REFERENCES AuthorBookRelationships (AuthorId, BookId)\n);