CREATE PROCEDURE sp_InsertCampaign\n    @name nvarchar(50),\n    @date datetime,\n    @organisation_1 nvarchar(50),\n    @organisation_2 nvarchar(50),\n    @organisation_3 nvarchar(50),\n    @organisation_4 nvarchar(50),\n    @organisation_5 nvarchar(50)\nAS\nBEGIN\n    -- Create a temporary table to store the 5 random records\n    CREATE TABLE #tmpOrganisations (\n        Key int,\n        organisation_name nvarchar(50)\n    );\n\n    -- Insert 5 random records from Tbl_Organisations into the temporary table\n    INSERT INTO #tmpOrganisations (Key, organisation_name)\n    SELECT TOP 5 Key, organisation_name\n    FROM Tbl_Organisations\n    ORDER BY NEWID();\n\n    -- Insert the data into Tbl_Campaigns\n    INSERT INTO Tbl_Campaigns (name, date, organisation_1, organisation_2, organisation_3, organisation_4, organisation_5)\n    VALUES (@name, @date, \n            (SELECT organisation_name FROM #tmpOrganisations WHERE Key = 1),\n            (SELECT organisation_name FROM #tmpOrganisations WHERE Key = 2),\n            (SELECT organisation_name FROM #tmpOrganisations WHERE Key = 3),\n            (SELECT organisation_name FROM #tmpOrganisations WHERE Key = 4),\n            (SELECT organisation_name FROM #tmpOrganisations WHERE Key = 5));\n\n    -- Drop the temporary table\n    DROP TABLE #tmpOrganisations;\nEND;