CREATE OR REPLACE FUNCTION create_lookup_table(p_column_name text)\nRETURNS void AS $$\nBEGIN\n  INSERT INTO lookup_metadata (column_name, lookup_table_name)\n  VALUES (p_column_name, 'lookup_' || p_column_name);\n\n  EXECUTE '\n    CREATE TABLE ' || 'lookup_' || p_column_name || ' (\n      id SERIAL PRIMARY KEY,\n      value VARCHAR(50) NOT NULL,\n      description VARCHAR(255) NOT NULL\n    );\n  ';\nEND;\n$$ LANGUAGE plpgsql;