CREATE TABLE #table (Id INT PRIMARY KEY, xxx INT);\nCREATE TABLE #table2 (Id INT PRIMARY KEY, xxx INT);\n\nCREATE INDEX idx_xxx ON #table (xxx);\nCREATE INDEX idx_xxx ON #table2 (xxx);\n\nINSERT INTO #table (Id, xxx) VALUES (1, 1), (2, 2), (3, 3);\nINSERT INTO #table2 (Id, xxx) VALUES (1, 1), (2, 2), (3, 3);\n\nIF EXISTS (\n    SELECT 1\n    FROM #table tbl\n    INNER JOIN #table2 tbl2 ON tbl.xxx = tbl2.xxx\n    WHERE tbl.xxx = 2\n)\nBEGIN\n    PRINT 'Rows exist';\nEND