CREATE PROCEDURE UpdateUserField\n    @UserFieldNumber INT, \n    @UserFieldNewValue INT\nAS\nBEGIN\n    DECLARE @sql NVARCHAR(MAX);\n    DECLARE @columnName SYSNAME;\n\n    SELECT @columnName = \n        CASE @UserFieldNumber\n            WHEN 1 THEN 'UserField1'\n            WHEN 2 THEN 'UserField2'\n            -- Add more cases as needed\n            ELSE NULL\n        END;\n\n    IF @columnName IS NULL\n    BEGIN\n        RAISERROR('Invalid UserFieldNumber', 16, 1);\n        RETURN;\n    END;\n\n    SET @sql = 'UPDATE MyTable SET ' + QUOTENAME(@columnName) + ' = @UserFieldNewValue';\n\n    EXEC sp_executesql @sql, N'@UserFieldNewValue INT', @UserFieldNewValue = @UserFieldNewValue;\nEND;