CREATE PROCEDURE UpdateTable\n    @ID INT,\n    @Column1 VARCHAR(50),\n    @Column2 INT\nAS\nBEGIN\n    IF @Column1 IS NOT NULL AND @Column1 <> (SELECT Column1 FROM YourTable WHERE ID = @ID)\n    BEGIN\n        UPDATE YourTable\n        SET Column1 = @Column1\n        WHERE ID = @ID\n    END\n\n    IF @Column2 IS NOT NULL AND @Column2 <> (SELECT Column2 FROM YourTable WHERE ID = @ID)\n    BEGIN\n        UPDATE YourTable\n        SET Column2 = @Column2\n        WHERE ID = @ID\n    END\nEND