-- Script database schema and data\nUSE [YourDatabaseName]\nGO\n\n-- Script schema\nSELECT 'CREATE TABLE [' + name + '] (' + \n       STUFF((SELECT ', ' + c.name + ' ' + t.name + ' ' + \n                     CASE WHEN c.is_nullable = 1 THEN 'NULL' ELSE 'NOT NULL' END\n              FROM sys.columns c\n              JOIN sys.types t ON c.system_type_id = t.system_type_id\n              WHERE c.object_id = OBJECT_ID('YourTableName')\n              FOR XML PATH('')), 1, 2, '') + ');'\nFROM sys.tables\nWHERE name = 'YourTableName';\n\n-- Script data\nSELECT 'INSERT INTO [' + name + '] (' + \n       STUFF((SELECT ', ' + c.name\n              FROM sys.columns c\n              WHERE c.object_id = OBJECT_ID('YourTableName')\n              FOR XML PATH('')), 1, 2, '') + ') VALUES (' + \n       STUFF((SELECT ', ' + CONVERT(VARCHAR(255), c.value)\n              FROM sys.columns c\n              WHERE c.object_id = OBJECT_ID('YourTableName')\n              FOR XML PATH('')), 1, 2, '') + ');'\nFROM sys.tables\nWHERE name = 'YourTableName';