CREATE TABLE main_table (\n  id NUMBER PRIMARY KEY,\n  date_column DATE\n) PARTITION BY RANGE (date_column) (\n  PARTITION p_2020 VALUES LESS THAN (TO_DATE('2021-01-01', 'YYYY-MM-DD')),\n  PARTITION p_2021 VALUES LESS THAN (TO_DATE('2022-01-01', 'YYYY-MM-DD')),\n  ...\n);\n\nCREATE TABLE subtable1 (\n  id NUMBER,\n  main_table_id NUMBER,\n  CONSTRAINT fk_subtable1_main_table FOREIGN KEY (main_table_id) REFERENCES main_table(id)\n) PARTITION BY REFERENCE (fk_subtable1_main_table);