SELECT ClientId, StudentType, Population, PopulationAdjustment, Sales, SalesAdjustment\nFROM (\n  SELECT ClientId, \n         StudentType1Population, StudentType1PopulationAdjustment, StudentType1Sales, StudentType1SalesAdjustment,\n         StudentType2Population, StudentType2PopulationAdjustment, StudentType2Sales, StudentType2SalesAdjustment,\n         -- ...\n  FROM ClientSales\n) AS SourceTable\nUNPIVOT (\n  Population FOR StudentType IN (\n    StudentType1Population, StudentType2Population, -- ...\n  )\n) AS UnPvtPopulation\nUNPIVOT (\n  PopulationAdjustment FOR StudentType IN (\n    StudentType1PopulationAdjustment, StudentType2PopulationAdjustment, -- ...\n  )\n) AS UnPvtPopulationAdjustment\nUNPIVOT (\n  Sales FOR StudentType IN (\n    StudentType1Sales, StudentType2Sales, -- ...\n  )\n) AS UnPvtSales\nUNPIVOT (\n  SalesAdjustment FOR StudentType IN (\n    StudentType1SalesAdjustment, StudentType2SalesAdjustment, -- ...\n  )\n) AS UnPvtSalesAdjustment