SELECT DISTINCT\n  A.APP_NO as app_no,\n  A.STATE as state\n  -- ... other fields\nFROM \n  TABLE_A A\n  LEFT OUTER JOIN TABLE_C B \n    ON A.COL1 = B.COL1\n  LEFT OUTER JOIN TABLE_D H \n    ON B.COL2 = H.COL2 AND A.STATE = H.COL3\n  LEFT OUTER JOIN TABLE_B HIST \n    ON A.COL1 = HIST.COL1\nWHERE\n  A.BRANCH = 'Data'