-- ASCII: Returns the ASCII value of a character\nSELECT ASCII('A')  -- Returns 65\n\n-- CHAR: Converts an ASCII value to a character\nSELECT CHAR(65)  -- Returns 'A'\n\n-- CHARINDEX: Finds the position of a substring within a string\nSELECT CHARINDEX('abc', 'xyzabc')  -- Returns 4\n\n-- LTRIM: Removes leading spaces from a string\nSELECT LTRIM('   Hello')  -- Returns 'Hello'\n\n-- RTRIM: Removes trailing spaces from a string\nSELECT RTRIM('Hello   ')  -- Returns 'Hello'\n\n-- LOWER: Converts a string to lowercase\nSELECT LOWER('HELLO')  -- Returns 'hello'\n\n-- UPPER: Converts a string to uppercase\nSELECT UPPER('hello')  -- Returns 'HELLO'\n\n-- PATINDEX: Finds the position of a pattern within a string\nSELECT PATINDEX('%abc%', 'xyzabc')  -- Returns 4\n\n-- REPLACE: Replaces all occurrences of a substring within a string\nSELECT REPLACE('abcabc', 'a', 'x')  -- Returns 'xbcxbc'\n\n-- REVERSE: Reverses the order of characters in a string\nSELECT REVERSE('hello')  -- Returns 'olleh'\n\n-- SUBSTRING: Extracts a portion of a string\nSELECT SUBSTRING('abcdefgh', 3, 2)  -- Returns 'cd'\n\n-- UNICODE: Returns the Unicode value of a character\nSELECT UNICODE('A')  -- Returns 65