-- CONCAT: Concatenates two or more strings\nSELECT CONCAT('Hello', ' ', 'World')  -- Returns 'Hello World'\n\n-- LEN: Returns the length of a string\nSELECT LEN('hello')  -- Returns 5\n\n-- QUOTENAME: Returns a string with square brackets added to make it a valid identifier\nSELECT QUOTENAME('my_table')  -- Returns '[my_table]'\n\n-- SOUNDEX: Converts a string to a phonetic code\nSELECT SOUNDEX('Robert')  -- Returns 'R163'\n\n-- SPACE: Returns a string of spaces\nSELECT SPACE(5)  -- Returns '     '\n\n-- STR: Converts a number to a string\nSELECT STR(123)  -- Returns '123'