-- Collect CPU usage\nSELECT cpu_count, cpu_usage * 100 AS cpu_usage_percent\nFROM sys.dm_os_performance_counters\nWHERE counter_name = 'SQLServer:CPU usage (percent)';\n\n-- Collect disk I/O metrics\nSELECT \n    reads = cntr_value,\n    writes = cntr_value\nFROM \n    sys.dm_os_performance_counters\nWHERE \n    counter_name IN ('SQLServer:Buffer Manager\Page reads/sec', 'SQLServer:Buffer Manager\Page writes/sec');