UPDATE s\nSET \n    s.OrgAddress1 = bd.OrgAddress,\n    s.OrgAddress2 = bd.OrgAddress2,\n    s.OrgCity = bd.OrgCity,\n    s.OrgState = bd.OrgState,\n    s.OrgZip = bd.OrgZip,\n    s.DestAddress1 = bd.DestAddress,\n    s.DestAddress2 = bd.DestAddress2,\n    s.DestCity = bd.DestCity,\n    s.DestState = bd.DestState,\n    s.DestZip = bd.DestZip\nFROM \n    SHIPMENT s\nINNER JOIN \n    ProfilerTest.dbo.BookingDetails bd ON s.MyID2 = @MyID2 AND bd.MyID = @MyID