SELECT \n    w.[System.Id],\n    w.[System.RevisedDate],\n    w.[System.Title],\n    w.[System.State],\n    w.[System.Reason],\n    w.[System.CreatedDate],\n    w.[System.WorkItemType],\n    w.[System.TeamProject],\n    w.[Microsoft.VSTS.Scheduling.RemainingWork],\n    w.[Microsoft.VSTS.Scheduling.CompletedWork],\n    w.[Microsoft.VSTS.CMMI.Estimate]\nFROM \n    WorkItems w\nINNER JOIN \n    (\n        SELECT \n            [System.Id], \n            MAX([System.RevisedDate]) AS LatestRevisedDate\n        FROM \n            WorkItems\n        WHERE \n            ([System.WorkItemType] = 'Change Request') \n            AND ([System.CreatedDate] >= '09/30/2008') \n            AND ([System.TeamProject] NOT LIKE '%Deleted%') \n            AND ([System.TeamProject] NOT LIKE '%Sandbox%')\n        GROUP BY \n            [System.Id]\n    ) latest\nON \n    w.[System.Id] = latest.[System.Id] \n    AND w.[System.RevisedDate] = latest.LatestRevisedDate\nWHERE \n    ([System.WorkItemType] = 'Change Request') \n    AND ([System.CreatedDate] >= '09/30/2008') \n    AND ([System.TeamProject] NOT LIKE '%Deleted%') \n    AND ([System.TeamProject] NOT LIKE '%Sandbox%')