-- Create a backup device\nEXEC sp_addumpdevice 'disk', 'FullBackup', 'C:\Backup\FullBackup.bak';\n\n-- Create a backup schedule\nCREATE TABLE BackupSchedule (\n    BackupType nvarchar(50),\n    Frequency nvarchar(50),\n    StartTime datetime,\n    EndTime datetime\n);\n\nINSERT INTO BackupSchedule (BackupType, Frequency, StartTime, EndTime)\nVALUES\n    ('Full', 'Weekly', '2023-03-01 02:00:00', '2023-03-01 04:00:00'),\n    ('Differential', 'Daily', '2023-03-01 02:00:00', '2023-03-01 04:00:00'),\n    ('Transaction Log', 'Every 15 minutes', '2023-03-01 02:00:00', '2023-03-01 04:00:00');\n\n-- Create a backup job\nCREATE PROCEDURE BackupDatabase\nAS\nBEGIN\n    DECLARE @BackupType nvarchar(50);\n    DECLARE @BackupFile nvarchar(255);\n\n    SET @BackupType = 'Full';\n    SET @BackupFile = 'C:\Backup\FullBackup.bak';\n\n    IF @BackupType = 'Full'\n        BEGIN\n            BACKUP DATABASE [YourDatabase] TO DISK = @BackupFile;\n        END;\n    ELSE IF @BackupType = 'Differential'\n        BEGIN\n            BACKUP DATABASE [YourDatabase] TO DISK = @BackupFile WITH DIFFERENTIAL;\n        END;\n    ELSE IF @BackupType = 'Transaction Log'\n        BEGIN\n            BACKUP LOG [YourDatabase] TO DISK = @BackupFile;\n        END;\nEND;\n\n-- Schedule the backup job\nEXEC sp_add_jobschedule @job_id=N'BackupDatabase', @name=N'BackupDatabase', \n        @enabled=1, \n        @freq_type=4, -- daily\n        @freq_interval=1, \n        @freq_recurrence_factor=0, \n        @active_start_time=20000, \n        @active_end_time=40000;