CREATE STREAM apply_stream;\n\nBEGIN\n  DBMS_STREAMS_ADM.ADD_TABLE_RULES(\n    stream_name       => 'apply_stream',\n    tables            => 'my_table',\n    inclusion_rule    => true);\n\n  DBMS_STREAMS_ADM.CREATE_APPLY(\n    apply_name        => 'apply_process',\n    apply_queue_name  => 'apply_queue');\n\n  DBMS_STREAMS_ADM.ADD_APPLY_RULE(\n    apply_name        => 'apply_process',\n    rule_name         => 'apply_rule',\n    rule_set_name     => 'apply_rule_set');\nEND;