Alter PROCEDURE [dbo].[GetUserDetailsByUserID]\n(\n    @UserId varchar(100)\n)\nAS\nBEGIN\n\n    IF EXISTS(Select * from Registration where UserId=@UserID)\n        BEGIN\n            Select\n                   [Name],\n                   [UserId],\n                   [PermanentAddress],\n                   [TemporaryAddress],\n                   [OfficePhoneNo],\n                   [ResidentialPhoneNo],\n                   [MobileNo],\n                   [Email],\n                   [ContactPerson],\n                   [C_OfficePhoneNo],\n                   [C_ResidentialPhoneNo],\n                   [C_MobileNo],\n                   [C_Email],\n                   [Project],\n                   [TotalAmount]\n            From\n                Registration\n            Where\n                UserId=@UserId\n        END\n    ELSE\n        BEGIN\n            SELECT TOP 0 NULL AS [Name], NULL AS [UserId], NULL AS [PermanentAddress], NULL AS [TemporaryAddress], NULL AS [OfficePhoneNo], NULL AS [ResidentialPhoneNo], NULL AS [MobileNo], NULL AS [Email], NULL AS [ContactPerson], NULL AS [C_OfficePhoneNo], NULL AS [C_ResidentialPhoneNo], NULL AS [C_MobileNo], NULL AS [C_Email], NULL AS [Project], NULL AS [TotalAmount]\n        END\n\nEND