DECLARE @dateDiff INT = #url.d#;\nDECLARE @found BIT = 0;\nDECLARE @result TABLE (/* define columns to match your 'news' table */);\n\nWHILE @found = 0\nBEGIN\n    INSERT INTO @result\n    SELECT * \n    FROM news\n    WHERE DATEDIFF(day, date, getdate() ) <= @dateDiff\n    ORDER BY date desc;\n\n    IF @@ROWCOUNT > 0\n        SET @found = 1;\n    ELSE\n        SET @dateDiff = @dateDiff + 1;\nEND;\n\nSELECT * FROM @result;