-- Use your database name here\nUSE YourDatabaseName\nGO\n\n-- Find the current SID for the dbo user\nSELECT SUSER_SID('domain\xzy') AS CurrentSID\n\n-- Drop the existing login\nDROP LOGIN [domain\xzy]\nGO\n\n-- Create a new login for the new domain account\nCREATE LOGIN [domain\abc] FROM WINDOWS\nGO\n\n-- Find the new SID for the new login\nSELECT SUSER_SID('domain\abc') AS NewSID\n\n-- Update the dbo user to use the new login\nALTER USER dbo WITH LOGIN = [domain\abc]\nGO