CREATE TRIGGER fkFooBar\n  BEFORE INSERT ON Foo_Bar\n  FOR EACH ROW BEGIN\n    SELECT RAISE (ABORT, 'Insert on Foo_Bar violates foreign key')\n    WHERE NOT EXISTS (\n      SELECT 1\n      FROM FOO\n      WHERE id = NEW.fooId\n    ) OR NOT EXISTS (\n      SELECT 1\n      FROM BAR\n      WHERE id = NEW.barId\n    );\n  END;