CREATE FUNCTION NormalizePostcode (@postcode nvarchar(10))\nRETURNS nvarchar(10)\nAS\nBEGIN\n    DECLARE @normalizedPostcode nvarchar(10)\n\n    -- Extract the first part of the postcode (before the space)\n    DECLARE @firstPart nvarchar(5)\n    SET @firstPart = LEFT(@postcode, CHARINDEX(' ', @postcode) - 1)\n\n    -- Pad the first part with a leading zero if necessary\n    SET @firstPart = CASE\n        WHEN LEN(@firstPart) = 2 THEN '0' + @firstPart\n        WHEN LEN(@firstPart) = 3 AND ISNUMERIC(RIGHT(@firstPart, 1)) = 1 THEN '0' + @firstPart\n        ELSE @firstPart\n    END\n\n    -- Combine the normalized first part with the rest of the postcode\n    SET @normalizedPostcode = @firstPart + SUBSTRING(@postcode, CHARINDEX(' ', @postcode), 10)\n\n    RETURN @normalizedPostcode\nEND