DELIMITER //\n\nCREATE PROCEDURE generate_dates(IN start_date DATE, IN end_date DATE)\nBEGIN\n  DECLARE current_date DATE;\n  SET current_date = start_date;\n  CREATE TEMPORARY TABLE dates (date DATE);\n\n  WHILE current_date <= end_date DO\n    INSERT INTO dates VALUES (current_date);\n    SET current_date = current_date + INTERVAL 1 DAY;\n  END WHILE;\n\n  SELECT * FROM dates;\nEND //\n\nDELIMITER ;