CREATE PROCEDURE [dbo].[GetData]\n    @ServerName sysname\nAS\nBEGIN\n    EXEC sp_addlinkedserver @server = @ServerName, @srvproduct = 'SQL Server'\n    EXEC sp_addlinkedsrvlogin @rmtsrvname = @ServerName, @useself = 'FALSE', @locallogin = 'sa', @rmtuser = 'sa', @rmtpassword = 'sa'\n    DECLARE @sql nvarchar(max)\n    SET @sql = N'SELECT * FROM OPENQUERY([' + @ServerName + '], ''SELECT * FROM ClientDataBase.dbo.Client'')'\n    EXEC sp_executesql @sql\nEND