-- Create tables\nCREATE TABLE PartNatural (\n    PartNumber VARCHAR(50) PRIMARY KEY,\n    Description VARCHAR(200)\n);\n\nCREATE TABLE PartSurrogate (\n    ID INT IDENTITY(1,1) PRIMARY KEY,\n    PartNumber VARCHAR(50) UNIQUE,\n    Description VARCHAR(200)\n);\n\n-- Create indexes\nCREATE INDEX idx_PartNumber ON PartNatural (PartNumber);\nCREATE INDEX idx_PartNumber ON PartSurrogate (PartNumber);\n\n-- Insert sample data\nINSERT INTO PartNatural (PartNumber, Description)\nVALUES ('ABC123', 'Sample part');\n\nINSERT INTO PartSurrogate (PartNumber, Description)\nVALUES ('ABC123', 'Sample part');\n\n-- Measure query performance\nSET STATISTICS TIME ON;\n\n-- Join query using surrogate key\nSELECT *\nFROM PartSurrogate ps\nJOIN Plant p ON ps.ID = p.PartID;\n\n-- Join query using natural key\nSELECT *\nFROM PartNatural pn\nJOIN Plant p ON pn.PartNumber = p.PartNumber;\n\nSET STATISTICS TIME OFF;