-- Create the FileDb database\nCREATE DATABASE FileDb;\nGO\n\n-- Create the Files table in FileDb\nUSE FileDb;\nCREATE TABLE Files (\n    FileId INT PRIMARY KEY,\n    FileName VARCHAR(255),\n    FileData VARBINARY(MAX)\n);\nGO\n\n-- Create the AppDb database\nCREATE DATABASE AppDb;\nGO\n\n-- Create a table in AppDb with a foreign key to FileDb\nUSE AppDb;\nCREATE TABLE AppData (\n    Id INT PRIMARY KEY,\n    FileId INT,\n    CONSTRAINT FK_AppData_Files FOREIGN KEY (FileId) REFERENCES FileDb.dbo.Files(FileId)\n);\nGO