-- Create the new clustered index\nCREATE CLUSTERED INDEX [IX_Clustered] ON [Table] \n(\n  [ColC] ASC,\n  [ColA] ASC,\n  [ColD] ASC,\n  [ColE] ASC,\n  [ColF] ASC,\n  [ColG] ASC\n)WITH (PAD_INDEX  = ON, STATISTICS_NORECOMPUTE  = OFF, SORT_IN_TEMPDB = OFF, \n       DROP_EXISTING = OFF, IGNORE_DUP_KEY = OFF, FILLFACTOR = 90, ONLINE = OFF, \n       ALLOW_ROW_LOCKS  = ON, ALLOW_PAGE_LOCKS  = OFF) ON [PRIMARY]\n\n-- Drop the existing primary key constraint\nALTER TABLE Table DROP CONSTRAINT [PK_Table]\n\n-- Create a new non-clustered primary key constraint\nALTER TABLE Table ADD CONSTRAINT \n  PK_Table PRIMARY KEY NONCLUSTERED \n  (\n    ColA,\n    ColB\n  ) WITH( STATISTICS_NORECOMPUTE = OFF, IGNORE_DUP_KEY = OFF, ALLOW_ROW_LOCKS = ON, \n          ALLOW_PAGE_LOCKS = ON) ON [PRIMARY]