WITH SampleData AS (\n  SELECT \n    TABLE_NAME, \n    col.DATA_TYPE, \n    ROW_NUMBER() OVER (PARTITION BY TABLE_NAME ORDER BY NEWID()) AS RowNum,\n    CONVERT(VARCHAR(MAX), col.value) AS SampleValue\n  FROM \n    INFORMATION_SCHEMA.COLUMNS col\n  CROSS APPLY \n    (SELECT TOP 3 value FROM your_table WHERE TABLE_NAME = col.TABLE_NAME) AS Sample\n)\nSELECT \n  TABLE_NAME, \n  DATA_TYPE, \n  STRING_AGG(SampleValue, ', ') AS Sample_Data\nFROM \n  SampleData\nGROUP BY \n  TABLE_NAME, \n  DATA_TYPE\nORDER BY \n  TABLE_NAME, \n  DATA_TYPE;